import axios from "axios";
import { createAction } from '@reduxjs/toolkit'

axios.defaults.baseURL = 'http://localhost:3000/'





const addContact = item => dispatch => {

    dispatch({type: 'contacts/addContactRequest'})
    axios
        .post('/contacts', item)
        .then(({ data }) =>
                dispatch({type: 'contacts/addContactSuccess', payload: data}),
            )
        .catch(error => dispatch({type: 'contacts/addContactError', payload: error}));
}

// const addContact = createAction('contacts/addContact')
const delContact = createAction('contacts/delContact')
const changeFilter = createAction('contacts/filteredContacts');

const contactsActions = {addContact, delContact, changeFilter}

export default contactsActions




