import { createAction } from '@reduxjs/toolkit'


const addContactRequest = createAction('contacts/addContactRequest');
const addContactSuccess = createAction('contacts/addContactSuccess');
const addContactError = createAction('contacts/addContactError');

const deleteContactRequest = createAction('contacts/deleteContactRequest');
const deleteContactSuccess = createAction('contacts/deleteContactSuccess');
const deleteContactError = createAction('contacts/deleteContactError');






// const addContact = createAction('contacts/addContact')
const delContact = createAction('contacts/delContact')
const changeFilter = createAction('contacts/filteredContacts');

const contactsActions = {delContact, changeFilter, addContactRequest,
    addContactSuccess, addContactError, deleteContactRequest, deleteContactSuccess, deleteContactError}

export default contactsActions




