import {combineReducers} from "redux";
import { createReducer } from '@reduxjs/toolkit'
import actions from './contacts-actions'
import axios from "axios";

const {addContactSuccess, delContactSuccess, changeFilter} = actions


// const KEY = 'contacts'
// const data = window.localStorage.getItem(KEY) || '[]';
// const initialItems = JSON.parse(data)
axios.defaults.baseURL = 'http://localhost:3000/'
const data = [];
axios.get('/contacts').then(response => data.push(response.data))

console.log(data)



const contacts = createReducer([], {
    [addContactSuccess]: (state, {payload}) => [...state, payload],
    [delContactSuccess]: (state, {payload}) => state.filter(contact => contact.id !== payload),
})


const filter = createReducer(data, {
    [changeFilter]: (_, {payload}) => payload,
})

export default combineReducers({
    contacts,
    filter,
})


